UID = #{uid}, TEMPLATE_ID = #{templateID}, RESUME_NAME = #{resumeName}, BASIC_INFO_COLUMNS = #{basicInfo}, FILE_PATH = #{filePath},  UP_USER = #{updateUser}, UP_DATETIME= #{updateDate}
where id = #{id} and uid = #{uid};

